package package4;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/* Build in class like Math
		 * 
		 */
		System.out.println("Absolute value of -5 is   : "+Math.abs(-5)); // return absolute value of a number
		System.out.println("Maximum value of -5 ,3 is : "+Math.max(-5,3)); // return max value of two number
		System.out.println("Minimum value of 3 , 2 is : "+Math.min(3,2)); // return min value of two number
		System.out.println("Round value of -5.5 is    : "+Math.round(-5.5)); // return round value of a number
		System.out.println("Squareroot of 25 is       : "+Math.sqrt(25)); // return squareroot of a number
		System.out.println("Cuberoot of 64 is         : "+Math.cbrt(64)); //return cuberoot of a number
		System.out.println(" 3 to the power of 3 is   : "+Math.pow(3,3)); // return 3 to the power of 3
		System.out.println("Ceil value of 23.6 is     : "+Math.ceil(23.6)); // return the ceiling value of a number
		System.out.println("Floor value of 24.9 is    : "+Math.floor(24.9)); // return the floor value of a number
		System.out.println("Logarithm  value of 3 is  : "+Math.log(3)); //return log value of a number
		System.out.println("Exponential value of 4 is : "+Math.exp(4)); // return exp value of a number
		
		
	}

}
